mapper.sql:
from ( select table_name, schema_name, tec_owner_user_name, busi_owner_user_name from table_meta_info_extra
and schema_name = #{db}
and table_name = #{table}
and dw_level = #{dwLevel}
) t1 join ( select id, table_name, schema_name, table_comment, table_size, table_total_size, table_last_modify_time, table_last_access_time from table_meta_info where assess_date = (select max(assess_date) from table_meta_info) ) t2 on t1.table_name = t2.table_name and t1.schema_name = t2.schema_name
